import React, { Component }  from 'react';
import { PostData } from '../../services/Methods/API';
import serializeForm from 'form-serialize';
import { BoxInput } from './BoxInput';

class BoxNewItem extends Component {
    constructor() {
      super();
      this.state = {
        list:[],
        errorAdd: "",
        errorSearch: "",
        countAdd: "",
      }

      this.handle = this.handle.bind(this);
    }

    handleError = (errors, type) => {
        let details = "";

        if (typeof errors.cpf !== "undefined") {
            details = errors.cpf.join("-")
        }

        if (typeof errors.cnpj !== "undefined") {
            details = errors.cnpj.join("-")
        }
console.log("details", details);
        if (type === "add") {
            this.setState({errorAdd: details})
            return ;
        }

        this.setState({errorSearch: details})    
    }

    handle = async (event) => {
        event.preventDefault();
        const formData = serializeForm(event.target, { hash: true })

        if (formData.document.length !== 11 && formData.document.length !== 14) {
            this.setState({errorAdd: "O cadastro requer um número com 11 ou 14 digitos."})
            return ;
        }

        if (formData.document.length === 11) {
            formData.cpf = formData.document;
        }

        if (formData.document.length === 14) {
            formData.cnpj = formData.document;
        }

        const save = await PostData(formData);

        if (save.status==="success") {
            this.props.newItem(save.data)

            return ;
        }

        if (save.status==="fail") {
            this.handleError(save.data);
        }
    }

    render() {
        const { errorAdd, countAdd } = this.state

        return <div className="row">
                    <div className="col-6">
                        <BoxInput 
                            btnDescription = "Adicionar"
                            countInput = {(input)=>{
                                if (input.target.value.length>0) {
                                    this.setState({countAdd: ` / (${input.target.value.length})`})
                                } else {
                                    this.setState({countAdd: ``})
                                }
                            }}
                            showInput = {countAdd}
                            buttonType = "btn-success"
                            handleForm = {this.handle}
                            description = "Adicionar CPF/CNPJ"
                            placeholderDescription = "Informe um CPF/CNPJ para cadastro"
                            errorMsg = {errorAdd}
                         />
                    </div>
                    {/* <div className="col-6">
                        <form onSubmit={ this.handle }>
                            <div className="my-3 p-3 bg-white rounded shadow-sm">
                                <h6 className="border-bottom border-gray pb-2 mb-0">Adicionar CPF/CNPJ</h6>
                                <span className="d-block text-right mt-3">
                                    <input className="form-control form-control-sm" maxLength={50} type="text" name="title" placeholder="Informe um CPF/CNPJ para cadastro" />
                                </span>
                                <div className="pt-2 text-right">
                                    <button type="submit" className="btn btn-success btn-sm" >Adicionar</button>
                                </div>
                            </div>
                        </form>
                    </div>
                    <div className="col-6">
                        <form onSubmit={ this.handle }>
                            <div className="my-3 p-3 bg-white rounded shadow-sm">
                                <h6 className="border-bottom border-gray pb-2 mb-0">Pesquisar CPF/CNPJ</h6>
                                <span className="d-block text-right mt-3">
                                    <input className="form-control form-control-sm" maxLength={50} type="text" name="title" placeholder="Informe um CPF ou CNPJ para pesquisar" />
                                </span>
                                <div className="pt-2 text-right">
                                    <button type="submit" className="btn btn-primary btn-sm" >Pesquisar</button>
                                </div>
                            </div>
                        </form>
                    </div> */}
                </div>
    }
}

export default BoxNewItem;
